<?php
$servername = "localhost";
$username = "root";
$password = "";
$dbname = "shop";

$conn = new mysqli($servername, $username, $password, $dbname);
if ($conn->connect_error) {
    die("Connection failed: " . $conn->connect_error);
}

// search và pagination
$search = isset($_GET['search']) ? $conn->real_escape_string($_GET['search']) : '';
$page = isset($_GET['page']) ? intval($_GET['page']) : 1;
$limit = 5;
$offset = ($page - 1) * $limit;

$sql = "SELECT * FROM products WHERE name LIKE '%$search%' OR description LIKE '%$search%' LIMIT $limit OFFSET $offset";
$result = $conn->query($sql);

$count_sql = "SELECT COUNT(*) AS total FROM products WHERE name LIKE '%$search%' OR description LIKE '%$search%'";
$count_result = $conn->query($count_sql);
$total = $count_result->fetch_assoc()['total'];
$total_pages = ceil($total / $limit);
?>

